import React, { useState } from 'react';
import './style.css';

function App() {
  const [tasks, setTasks] = useState([]);
  const [inputValue, setInputValue] = useState('');

  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  };

  const addTask = () => {
    if (inputValue.trim() !== '') {
      setTasks([...tasks, inputValue.trim()]);
      setInputValue('');
    }
  };

  const removeTask = (index) => {
    setTasks((prevState) => prevState.filter((_, i) => i !== index));
  };

  const completeTask = (index) => {
    setTasks((prevState) =>
      prevState.map((task, i) =>
        i === index ? <s key={i}>{task}</s> : task
      )
    );
  };

  const handleKeyDown = (event) => {
    if (event.key === 'Enter') {
      event.preventDefault();
      addTask();
    }
  };

  return (
    <div className="container">
      <h1>To-Do List</h1>
      <form>
        <input
          type="text"
          placeholder="Add a task..."
          value={inputValue}
          onChange={handleInputChange}
          onKeyDown={handleKeyDown}
          required
        />
        <button type="button" onClick={addTask}>
          Add
        </button>
      </form>
      <ul>
        {tasks.map((task, index) => (
          <li key={index}>
            <span className={task.includes('<s>') ? 'completed' : ''}>
              {task}
            </span>
            <div>
              <button className="completeBtn" onClick={() => completeTask(index)}>
                ✔
              </button>
              <button className="removeBtn" onClick={() => removeTask(index)}>
                ❌
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
